const interval = setInterval(() => {
    let viewport = document.querySelector('meta[name="viewport"]')
    const masthead = document.getElementById("masthead")
    if (masthead) masthead.remove()

    const footer = document.querySelector("footer")
    if (footer) footer.remove()
    const heroSection = document.querySelector(
      ".entry-hero.page-hero-section.entry-hero-layout-standard",
    )
    if (heroSection) heroSection.remove()

    const appSection = document.querySelector(
      ".kb-row-layout-id2541_a48faa-06",
    )
    if (appSection) appSection.remove()

    // Stop interval when everything is gone
    if (
      !document.getElementById("masthead") &&
      !document.querySelector("footer") &&
      !document.querySelector(".entry-hero.page-hero-section.entry-hero-layout-standard")
    ) {
    }

    const links = document.querySelectorAll("a[href]")

    links.forEach((link) => {
      // Option 1: Remove the whole tag
      link.remove()

      // Option 2: If you want to KEEP the text but disable navigation:
      // link.removeAttribute('href');
      // link.style.pointerEvents = 'none';
      // link.style.cursor = 'default';
    })

    if (viewport) {
      viewport.setAttribute("content", "width=device-width, initial-scale=0.7, minimum-scale=0.7")
    }
    clearInterval(interval)
  }, 40)

